import React, { useEffect, useState } from 'react'
import './App.css';
import Preview from './components/Preview'
import Message from './components/Message'
import NotesContainer from './components/Notes/NotesContainer'
import NotesList from './components/Notes/NotesList';
import Note from './components/Notes/Note'
import NoteForm from './components/Notes/NoteForm';


function App() {

const [notes,setNotes]           = useState([])
const [title,setTitle]           = useState('')
const [content,setContent]       = useState('')
const [selectedNote,setSelectedNote] = useState(null)
const [creating,setCreating]     = useState(false)
const [editing,setEditing]       = useState(false)

useEffect(()=>{
  if (localStorage.getItem('notes')) {
    setNotes(JSON.parse(localStorage.getItem('notes')))
  } else {
    localStorage.setItem('notes',JSON.stringify([]))
  }
}, [])

const saveToLocalStorage = (key,value) => {
  localStorage.setItem(key,JSON.stri)
}

// تغييير عنوان الملاحظة
const changeTitleHandler = (e) =>{
  setTitle(e.target.value)
}

// تغييير عنوان نص الملاحظة
const changeContentHandler = (e) =>{
  setContent(e.target.value)
}

// حفظ الملاحظة و المحتوى
const saveNoteHandler = () => {
  const note = {
    id: new Date(),
    title: title,
    content: content
  }

  const updateNotes = [...notes, note]

  setNotes(updateNotes)
  setCreating(false)
  setSelectedNote(note.id)
  setTitle('')
  setContent('')
}

// إختيار ملاحظة
const selectNoteHandler = (noteId) => {
  setSelectedNote(noteId)
  setCreating(false)
  setEditing(false)
}

// تعديل الملاحظة
const editNoteHandler = () => {
  const note = notes.find(note => note.id === selectedNote)
  setEditing(true)
  setContent(note.content)
  setTitle(note.title)
}

// تعديل ملاحظة
const updateNoteHandler = () => {
  const updatedNotes = [...notes]
  const noteIndex = notes.findIndex(note => note.id === selectedNote)
  updatedNotes[noteIndex] = {
    id: selectedNote,
    title: title,
    content: content
  };

  setNotes(updatedNotes)
  setEditing(false)
  setTitle('')
  setContent('')
}

// الإنتقال إلى واجهة إضافة ملاحظة
const addNoteHandler = () => {
  setCreating(true)
  setEditing(false)
  setTitle('')
  setContent('')
 }

//  حذف ملاحظة
const deleteNoteHandler = () =>{
  const updatedNotes = [...notes]
  const findIndex = updatedNotes.findIndex(note => note.id === selectedNote)
  notes.splice(findIndex,1)
  setNotes(notes)
  setSelectedNote(null)
}

  const getAddNote = () => {
    return (
      <NoteForm
        formTitle = 'ملاحظة جديدة'
        title = {title}
        content = {content}
        titleChanged = {changeTitleHandler} 
        contentChanged = {changeContentHandler}
        submitText = 'حفظ'
        submitClicked = {saveNoteHandler}
      />
    );
  };

  const getPreview = () => {
    if (notes.length == 0) {
      return <Message title='لا يوجد ملاحظة' />

    }

    if (!selectedNote) {
      return <Message title='لا يوجد ملاحظةالرجاء إختيار ملاحظة ' />
    }

    const note = notes.find(note => {
      return note.id === selectedNote
    })

    let noteDisplay = (
      <div>
        <h2>{note.title}</h2>
        <p>{note.content}</p>
      </div>
    )

    if (editing) {
      noteDisplay = (
        <NoteForm
          formTitle = 'تعديل ملاحظة'
          title = {title}
          content = {content}
          titleChanged = {changeTitleHandler} 
          contentChanged = {changeContentHandler}
          submitText = 'تعديل'
          submitClicked = {updateNoteHandler}
        />
      )
    }

    return (
      <div>
        { !editing &&
          <div className="note-operations">
            <a href="#" onClick={editNoteHandler}>
              <i className="fa fa-pencil-alt" />
            </a>
            <a href="#" onClick={deleteNoteHandler}>
              <i className="fa fa-trash" />
            </a>
        </div>
        }

        {noteDisplay}
      </div>
    );
  };

  return (
    <div className="App">
      <NotesContainer>
        <NotesList>
          {
            notes.map(note => (
              <Note 
                key = {note.id} 
                title = {note.title} 
                noteClicked = {()=> selectNoteHandler(note.id)} 
                active = {selectedNote === note.id}
              />
            ))
          }
        </NotesList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </NotesContainer>
      <Preview>
        {creating ? getAddNote() : getPreview()}
      </Preview>
    </div>
  );
}

export default App;
